package boucles;

public class EXOwhileboucle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int nombre = 1 ;
		while (nombre <= 100 ) {
		  System.out.println("la valeur de nombre = "  + nombre );
		  nombre++;
		
		}
		
		
		int nombre01 = 100 ;
		while (nombre01 >= 1 ) {
		  System.out.println("la valeur de nombre = "  + nombre01 );
		  nombre01--;
		
		
		}
		
		
		
		int nombre02 = 2 ;
		while (nombre02 <= 100 ) {
		  System.out.println("la valeur de nombre = "  + nombre02 );
		  nombre02 =  nombre02 + 2;
		
		
		}
		
		int nombre03 = 100 ;
		while (nombre03 >= 2 ) {
		  System.out.println("la valeur de nombre = "  + nombre03 );
		  nombre03 =  nombre03 - 2;
		
		  
		}  
		  
		int nbr1 = 1 ,  nbr2 = 100 ;
		while (nbr1 <= 100 && nbr2  >= 1) {
			System.out.println("la valeur de nombre = " + nbr1 +  "la valeur de nombre = " + nbr2);
			nbr1++;
			nbr2--;
			
		}
		  
		  
		  
		  
		  
		  
		  
		  
		  
		  
		  
		  
	}

}
